DECLARE @sql NVARCHAR(MAX);
DECLARE @TableName NVARCHAR(128) = 'Customers';

SET @sql = N'SELECT * FROM ' + QUOTENAME(@TableName) + 
          N' WHERE Status = @Status';

EXEC sp_executesql @sql,
     N'@Status varchar(20)',
     @Status = 'Active';

-- Horizontal Partitioning Example
CREATE TABLE Customers_USA PARTITION OF Customers
    FOR VALUES IN ('USA');

-- Vertical Partitioning Example
CREATE TABLE Customer_Basic (
    customer_id INT,
    name VARCHAR(100),
    email VARCHAR(100)
);

CREATE TABLE Customer_Details (
    customer_id INT,
    profile_photo BLOB,
    biography TEXT
);

SELECT ProductID, [2021], [2022]  
FROM (
    SELECT ProductID, YEAR(SaleDate) AS SaleYear, Amount  
    FROM Sales  
) AS Source  
PIVOT (
    SUM(Amount)  
    FOR SaleYear IN ([2021], [2022])  
) AS PivotTable;
